function generateclicknoty(type, text, position) {
			var n = noty({
				text        : text,
				type        : type,
				layout: position,
				closeWith   : ['click'],
				theme       : 'customnoty',                
				animation   : {
					open  : 'animated bounceInRight',
					close : 'animated bounceOutLeft',
					easing: 'swing',
					speed : 1000
				}
			});

 }

 function generatetimeoutnoty(type, text, position, timeout) {

			var n = noty({
				text        : text,
				type        : type,
				layout: position,
				timeout: 5000,
				theme       : 'customnoty',                
				animation   : {
					open  : 'animated bounceInRight',
					close : 'animated bounceOutLeft',
					easing: 'swing',
					speed : 1000
				}
			});

 }

  // Accordion Open
  function accordionOpen(object) {
		$panel_headers = object.find('> li > .collapsible-header');
		if (object.hasClass('active')) {
			object.parent().addClass('active');
		}
		else {
			object.parent().removeClass('active');
		}
		if (object.parent().hasClass('active')){
			var staggeredlists = object.find('#staggered');
			if (staggeredlists.length > 0) {
				object.siblings('.collapsible-body').stop(true,false).slideDown({ duration: 350, easing: "easeInQuint", queue: false, start: function() { Materialize.showStaggeredList('#staggered');}, complete: function() {$(this).css('height', '');}});
			}
			else{
				object.siblings('.collapsible-body').stop(true,false).slideDown({ duration: 350, easing: "easeInQuint", queue: false, complete: function() {$(this).css('height', '');}});
				$panel_headers.find('.accordion-toggle').text('remove');
			}
		  
		}
		else{
		  object.siblings('.collapsible-body').stop(true,false).slideUp({ duration: 350, easing: "easeOutQuint", queue: false, complete: function() {$(this).css('height', '');}});
		  $panel_headers.find('.accordion-toggle').text('add');
		}
		$panel_headers.not(object).find('.accordion-toggle').text('add');
		$panel_headers.not(object).removeClass('active').parent().removeClass('active');
		$panel_headers.not(object).parent().children('.collapsible-body').stop(true,false).slideUp(
		  {
			duration: 350,
			easing: "easeOutQuint",
			queue: false,
			complete:
			  function() {
				$(this).css('height', '');
			  }
		  });
	  }


	  
